import unittest
import os
import multicrawler as Crawler


class TestCanonizationUrl(unittest.TestCase):
    URL_NORMAL = 'http://crawlers.info'

    def test_canonization_slashes_url(self):
        url_slashes = r'http://crawlers.info////'
        self.assertEqual(
            Crawler.canonization_url(url_slashes),
            TestCanonizationUrl.URL_NORMAL)

    def test_canonization_dots_url(self):
        url_dots = 'http://crawlers.info...'
        self.assertEqual(
            Crawler.canonization_url(url_dots),
            TestCanonizationUrl.URL_NORMAL)

    def test_canonization_url_port(self):
        url_port = 'http://crawlers.info:80/'
        self.assertEqual(
            Crawler.canonization_url(url_port),
            TestCanonizationUrl.URL_NORMAL)

    def test_canonization_dots_slashes_url(self):
        url_slashes_dots = 'http://crawlers.info/./././..'
        self.assertEqual(
            Crawler.canonization_url(url_slashes_dots),
            TestCanonizationUrl.URL_NORMAL)


class TestCrawlerModule(unittest.TestCase):
    def test_refs_cutting(self):
        actual = [
            'accounts/login', 'accounts/register', 'school/goto',
            'school/polymus', 'school/apo', 'school/bsu', 'school/hse',
            'school/hsedpo', 'school/g1576', 'school/msu', 'school/mipt',
            'school/nsu', 'school/yandexukraineforagencies', 'school/aesc_msu',
            'school/pytraining', 'school/urfu', 'school/shad', 'school/mobdev',
            'school/shpya', 'school/product', 'school/ml27', 'blog',
            'mailto:anytask@yandex.ru']
        self.assertListEqual(actual, Crawler.cut_urls('https://anytask.org/'))

    def test_crawler_creating(self):
        c = Crawler.Crawler(['https://anytask.org/'])
        c.crawl()
        self.assertListEqual(list(c.checked), ['https://anytask.org/'])

    def test_may_fetch(self):
        pages = [
            'https://www.baristainstitute.com/filter/tips',
            'https://www.baristainstitute.com',
            'https://www.baristainstitute.com/inspiration']
        expected = [
            'https://www.baristainstitute.com',
            'https://www.baristainstitute.com/inspiration']
        actual = list(filter(
            lambda page: Crawler.may_fetch(
                'https://www.baristainstitute.com/robots.txt', page), pages))
        self.assertListEqual(expected, actual)

    def test_writing_in_json(self):
        pth = os.path.join(os.getcwd(), "test_file.json")
        d = {'links': {'current': 'http:/prikol', 'queue': ['meme1', 'meme2']}}
        Crawler.write_current_link_in_json(pth, d)
        with open(pth, 'r') as jf:
            line = jf.readline()
            self.assertEqual(line, str(d).replace("'", "\""))

    def test_reading_out_of_json_file(self):
        pth = os.path.join(os.getcwd(), "test_file.json")
        recv = Crawler.get_current_link_and_links(pth)
        self.assertEqual(recv[0], 'http:/prikol')
        self.assertListEqual(['meme1', 'meme2'], recv[1])

    def test_parsing_site_without_images(self):
        url = "https://www.respectme.ru/anecdote/mathematical"
        self.assertEqual(None, Crawler.cut_images(url))

    def test_normalizing_uncompleted(self):
        exp = ["https://www.baristainstitute.com/sites/default/themes/barista/images/paulig-barista-logo.svg",
               "https://www.baristainstitute.com/sites/default/themes/barista/logo.svg",
               "https://www.baristainstitute.com/sites/default/ista-logo.svg",
               "https://www.baristainstitute.com/sites/default/themes/barista/images/paul787dsig-svdsdc-logo.svg",
               "https://www.baristainstitute.com/sites/default/themes/vsd/sdv/paulig-jsbd-logo.svg"]
        uncompleted = ["/sites/default/themes/barista/images/paulig-barista-logo.svg",
                       "/sites/default/themes/barista/logo.svg", "/sites/default/ista-logo.svg",
                       "/sites/default/themes/barista/images/paul787dsig-svdsdc-logo.svg",
                       "/sites/default/themes/vsd/sdv/paulig-jsbd-logo.svg"]
        act = Crawler.normalize_references(uncompleted, "https:", "www.baristainstitute.com")
        self.assertListEqual(act, exp)

