class TestCuttingImagesAndContent(unittest.TestCase):
    def test_norming_file_name_no_format(self):
        supposed = "file_name_with_no_format"
        expected = supposed + ".jpg"
        act = Crawler.norm_file_name(supposed)
        self.assertEqual(act, expected)

    def test_norm_file_name_with_format(self):
        supposed = "file_name_with_no_format.svg"
        expected = supposed
        act = Crawler.norm_file_name(supposed)
        self.assertEqual(act, expected)

    def test_norm_file_name_format_in_middle(self):
        supposed = "file_name_with.jpeg_no_format"
        expected = supposed + ".jpg"
        act = Crawler.norm_file_name(supposed)
        self.assertEqual(act, expected)


if __name__ == '__main__':
    unittest.main()
